mapper.insert:
set names utf8mb4;
insert into member_info(member_id, head, name, mobile, open_id, create_time, state)
values(#{memberId}, #{head}, #{name}, #{mobile}, #{openId}, #{createTime}, #{state})

mapper.update:
set names utf8mb4; update member_info
head = #{head}, name = #{name}, mobile = #{mobile}, open_id = #{openId}, create_time = #{createTime}, state = #{state},
where member_id = #{memberId}

mapper.select:
	set names utf8mb4;
  select

  from member_info
  where member_id = #{memberId}
